import React from "react";
import { motion } from "framer-motion";
import "./About.css";

import b2 from "./assets/b2.jpeg";
import b3 from "./assets/b3.jpeg";

export default function About() {
  return (
    <section className="about-section">
      <div className="container">
        <div className="row align-items-center">
          {/* Left Side - Overlapping Images */}
          <div className="col-md-6 position-relative about-images">
            <motion.img
              src={b2}
              alt="About Us 1"
              className="about-img about-img-1"
              whileHover={{ scale: 1.1, rotate: 5 }} // Hover Animation
              transition={{ type: "spring", stiffness: 200 }}
            />
            <motion.img
              src={b3}
              alt="About Us 2"
              className="about-img about-img-2"
              whileHover={{ scale: 1.1, rotate: -5 }} // Hover Animation
              transition={{ type: "spring", stiffness: 200 }}
            />
          </div>

          {/* Right Side - Text */}
          <div className="col-md-6">
            <h2 className="about-title">About Us</h2>
            <p className="about-text">
              At <strong>Aeternus Energy</strong>, we are committed to providing
              innovative and sustainable energy solutions. Our goal is to power
              the future with clean, renewable energy sources.
            </p>
            <p className="about-text">
              With years of experience in solar and green energy, we take pride
              in offering high-quality products and services tailored to meet
              our customers' needs.
            </p>
          </div>
        </div>
      </div>
    </section>
  );
}
