import React from "react";
import { motion, useAnimation } from "framer-motion";
import { useInView } from "react-intersection-observer";
import "./About.css";
import b3 from "./assets/b3.jpeg";

export default function About() {
  const controls1 = useAnimation();
  const controls2 = useAnimation();
  const [ref, inView] = useInView({ triggerOnce: false, threshold: 0.5 });

  React.useEffect(() => {
    if (inView) {
      controls1.start({
        y: [0, 150],
        x: [0, 50],
        transition: { duration: 1, ease: "easeInOut" },
      });

      controls2.start({
        y: [0, -150],
        x: [0, -50],
        transition: { duration: 1, ease: "easeInOut" },
      });
    }
  }, [inView, controls1, controls2]);

  return (
    <section className="about-section" ref={ref}>
      <div className="container">
        <h2 className="about-title">Who We Are</h2>
        <div className="about-content">
          <div className="about-images">
            <motion.img
              src={b3}
              alt="About Us 1"
              className="about-img about-img-1"
              animate={controls1}
            />
            <motion.img
              src={b3}
              alt="About Us 2"
              className="about-img about-img-2"
              animate={controls2}
            />
          </div>
          <div className="about-text">
            <p>
              <strong>Aeternus Energy LLP</strong> is an innovative startup
              dedicated to advancing battery technology through cutting-edge
              battery pack and module enclosure design. We also develop battery
              management software (BMS) and energy management solutions for
              small-scale and large utility-scale battery systems.
            </p>
            <p>
              By integrating intelligent software with robust hardware, we
              optimize energy storage performance, enhance safety, and drive
              efficiency in battery-powered applications for residential,
              commercial, and grid-scale energy projects.
            </p>
          </div>
        </div>
      </div>
    </section>
  );
}
